import 'bootstrap';
import Echo from 'laravel-echo';

/**
 * We'll load the axios HTTP library which allows us to easily issue requests
 * to our Laravel back-end. This library automatically handles sending the
 * CSRF token as a header based on the value of the "XSRF" token cookie.
 */

import axios from 'axios';
window.axios = axios;

window.axios.defaults.headers.common['X-Requested-With'] = 'XMLHttpRequest';



window.Pusher = require('pusher-js');

window.Echo = new Echo({
    broadcaster: 'pusher',
    key: process.env.MIX_PUSHER_APP_KEY,
    cluster: process.env.MIX_PUSHER_APP_CLUSTER,
    forceTLS: true,
});

window.Echo.channel('crypto-prices')
    .listen('CryptoPriceUpdated', (event) => {
        const cryptoId = event.crypto.id;
        const newPrice = event.crypto.current_price;

        // Find the row in the table corresponding to the crypto ID and update the price
        const row = document.querySelector(`tr[data-id="${cryptoId}"]`);
        if (row) {
            row.querySelector('.crypto-price').textContent = `$${newPrice.toFixed(2)}`;
        }
    });



/**
 * Echo exposes an expressive API for subscribing to channels and listening
 * for events that are broadcast by Laravel. Echo and event broadcasting
 * allows your team to easily build robust real-time web applications.
 */

